        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-0"&gt;col-0&lt;/div&gt;
        &lt;div class="col col-1"&gt;col-1&lt;/div&gt;
        &lt;div class="col col-1"&gt;col-1&lt;/div&gt;
        &lt;div class="col col-1"&gt;col-1&lt;/div&gt;
        &lt;div class="col col-1"&gt;col-1&lt;/div&gt;
        &lt;div class="col col-1"&gt;col-1&lt;/div&gt;
        &lt;div class="col col-1"&gt;col-1&lt;/div&gt;
        &lt;div class="col col-2"&gt;col-2&lt;/div&gt;
        &lt;div class="col col-2"&gt;col-2&lt;/div&gt;
        &lt;div class="col col-2"&gt;col-2&lt;/div&gt;
        &lt;div class="col col-2"&gt;col-2&lt;/div&gt;
        &lt;div class="col col-3"&gt;col-3&lt;/div&gt;
